package com.datastructure.stringsbased;

public class StarPlay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final String charValue = "_";
		for (int i = 0; i < 5; i++) {
			for (int j = i; j < 5; j++) {
				System.out.print(charValue);
			}
			System.out.println("");
		}
	}

}
