package com.interviews.problems;

/*
 Convert a Binary into Integer if odd - 1
 if even
 recursion
 */
public class UnifonicBinaryToDivision {

    public static void main(String[] args) {
        //String s = "";
        String s = "111";
        int v = Integer.parseUnsignedInt(s, 2);
        System.out.println(v);
        int steps = recursiveCheck(v, 0);
        System.out.println(steps);
    }

    //public static int BinaryToInt()

    public static int recursiveCheck(int v, int steps) {
        if (v == 0)
            return steps - 1;

        if (v % 2 == 0) {
            v = v / 2;
        }
        else if (v % 2 == 1){
            v = (v - 1) / 2;
            steps = steps + 1;
        }
        steps = steps + 1;
        return recursiveCheck(v , steps);
    }


}
